CREATE TABLE IF NOT EXISTS message_replies (id uuid DEFAULT uuid_generate_v4() PRIMARY KEY,message_id uuid REFERENCES messages(id) ON DELETE CASCADE,user_id text NOT NULL,text text NOT NULL,original_text text NOT NULL,created_at timestamp with time zone DEFAULT timezone('utc'::text, now()) NOT NULL);CREATE INDEX IF NOT EXISTS message_replies_message_id_idx ON message_replies(message_id);CREATE INDEX IF NOT EXISTS message_replies_user_id_idx ON message_replies(user_id);CREATE INDEX IF NOT EXISTS message_replies_created_at_idx ON message_replies(created_at);
